import { AcGameObject } from "./AcGameObject";

export class GameMap extends AcGameObject {
    constructor(ctx,parent) {
        super();

        this.ctx = ctx;
        this.parent = parent;
        this.L = 0;

        this.rows = 13;
        this.cols = 13;
    }

    start() { 

    }
    update_size() {
        this.L = Math.min(this.parent.clientWidth / this.cols, this.parent.height / this.rows);
    }
    update() { 
        this.render();
    }

    render() {
        
    }
}
